class Solution:
    def findSubstring(self, s: str, words: list) -> list:
        results = set()
        length = len("".join(words))
        searched_words = set()
        for index, word in enumerate(words):
            if word in searched_words:
                continue
            searched_words.add(word)
            word_indexes = [index_found for index_found in range(len(s)) if s.startswith(word, index_found)]
            search_words_ = sorted(words[:index] + words[index + 1:], key=lambda x: len(x), reverse=True)
            for index_found in word_indexes:
                if index_found in results:
                    continue
                if index_found + length > len(s):
                    continue
                current_substring = s[index_found + len(word):index_found + length]
                search_words = search_words_[:]
                while len(search_words) > 0 and len(current_substring) != 0:
                    found = False
                    for s_index, s_word in enumerate(search_words):
                        if current_substring.startswith(s_word):
                            search_words = search_words[:s_index] + search_words[s_index + 1:]
                            current_substring = current_substring[len(s_word):]
                            found = True
                            break
                    if not found:
                        break
                if len(search_words) == 0:
                    results.add(index_found)
        return list(results)

s = Solution()
