class Solution:
    def is_palindrome(self, s):
        index = 0
        while index < (len(s) / 2):
            if s[index : len(s) - index] in self.palindroms:
                if index == 0:
                    return True
                break
            if s[index] != s[len(s) - index - 1]:
                return False
            index += 1
        self.palindroms.append(s)
        return True
    def otherSolution(self, s):
        self.palindroms = []
        max_size = 0
        longest_pal = ""
        index = 0
        while index < len(s):
            size = len(s) - index
            if size < max_size:
                break
            index_sl = index + 1
            while index_sl < len(s) and s[index_sl] == s[index_sl - 1] and self.is_palindrome(s[index : index_sl]):
                index_sl += 1
            if (index_sl - index) > max_size:
                max_size = index_sl - index
                longest_pal = s[index : index_sl]
            if (index_sl - index) >= (size - 1):
                return longest_pal
            if (index_sl - index) <= int(size / 2):
                while size > max_size:
                    sub_s = s[index : index + size]
                    if self.is_palindrome(sub_s):
                        longest_pal = sub_s
                        max_size = size
                        if max_size >= len(s) - 1:
                            return longest_pal
                        break
                    size -= 1
            index += 1
        return longest_pal
    def longestPalindrome(self, s):
        if (len(s) < 1):
            return ""
        start = 0
        end = 0
        for i in range(0, len(s)):
            len1 = self.expandAroundCenter(s, i, i)
            len2 = self.expandAroundCenter(s, i, i + 1)
            length = max(len1, len2)
            if length > (end - start + 1):
                start = i - int((length - 1) / 2)
                end = i + int(length / 2)
        return s[start : end + 1]
    def expandAroundCenter(self, s, left, right):
        L = left
        R = right
        while L >= 0 and R < len(s) and s[L] == s[R]:
            L -= 1
            R += 1
        return R - L - 1

s = Solution()
print(s.longestPalindrome("aaaabaaa") == "aaabaaa")
print(s.longestPalindrome("abacab") == "bacab")
print(s.longestPalindrome("a") == "a")
print(s.longestPalindrome("babad") == "bab")
print(s.longestPalindrome("cbbd") == "bb")
print(s.longestPalindrome("") == "yvvy")
print(s.longestPalindrome("") == "")
print(s.longestPalindrome("") == "")
print(s.longestPalindrome("") == "")
